double check_leaderboard_file(const vector<std::pair<std::string, int>> &expected, const char * filename) {
    double score = 0;
    int i = 0;

    std::ifstream file(filename);

    // Check if the file is opened successfully
    if (!file.is_open()) {
        std::cerr << "Error opening file: " << filename << std::endl;
        return 0;  // Return an error code
    }

    std::string line;
    while (std::getline(file, line)) {
        vector<string> split_line = split(line, " ");
        if (stoi(split_line[0]) == expected[i].second && split_line[2] == expected[i].first) {
            score += 1;
        }
        i++;
    }

    // Close the file
    file.close();

    return score;
}



double check_leaderboard(const vector<std::pair<std::string, int>> &expected, LeaderboardEntry *head) {

    int i = 0;
    double score = 0;

    LeaderboardEntry *current = head;

    while (current != nullptr) {
        if (current->player_name == expected[i].first && current->score == expected[i].second) {
            score += 1;
        }
        current = current->next_leaderboard_entry;
        i++;
    }
    return score;
}


