double test_init_blocks() {
    double score = 0;
    double score_blocks = 0;
    double score_rotations = 0;

    int total_number_blocks = 5;
    int total_number_rotations = 11;

    vector<vector<vector<bool>>> expected_blocks = {
            {{true,  false, true}, {true,  true,  true}},
            {{true}},
            {{true,  true}},
            {{true,  true,  true}},
            {{true,  true,  true,  true}},
            {{true,  true}, {true,  true}}
    };

    vector<vector<vector<vector<bool>>>> expected_rotations = {
            {
                    {{true,false,true},{true,true,true}},
                    {{true,true},{true,false},{true,true}},
                    {{true,true,true},{true,false,true}},
                    {{true,true},{false,true},{true,true}},
            },
            {
                    {{true}},
            },
            {
                    {{true,true}},
                    {{true},{true}},
            },
            {
                    {{true,true,true}},
                    {{true},{true},{true}},
            },
            {
                    {{true,true,true,true}},
                    {{true},{true},{true},{true}},
            },
            {
                    {{true,true},{true,true}},
            }
    };


    BlockFall game("testsIO/grid.dat", "testsIO/blocks_init_blocks.dat", false, "testsIO/leaderboard_tmp.txt",
                   "alperen");


    // Check blocks
    int i = 0;
    Block *current = game.initial_block;
    while (current != nullptr) {
        if (current->shape == expected_blocks[i]) {
            score_blocks += 1;
        }
        current = current->next_block;
        i++;
    }

    // Check rotations
    i = 0;

    current = game.initial_block;
    while (current != nullptr) {

        // Check rotations
        int j = 0;
        Block *current_block = current;
        do {
            if (current_block->shape == expected_rotations[i][j]) {
                score_rotations += 1;
            }
            current_block = current_block->right_rotation;
            j++;

        } while (current_block != current);
        current = current->next_block;
        i++;
    }

    // Calculate score
    score_blocks = score_blocks / total_number_blocks;
    score_rotations = score_rotations / total_number_rotations;
    score = 0.3 * score_blocks + 0.7 * score_rotations;

    return score;
}

