double test_game_over() {
    double score = 0;
    int total_number = 3;

    // Out of blocks
    BlockFall game("testsIO/grid.dat", "testsIO/blocks_game_over_1.dat", false, "testsIO/leaderboard_tmp.txt", "alperen");
    GameController controller1;

    bool stat = controller1.play(game, "testsIO/commands_game_over_1.dat");
    if (stat) score += 1;

    // Game over
    BlockFall game2("testsIO/grid.dat", "testsIO/blocks_game_over_2.dat", false, "testsIO/leaderboard_tmp.txt", "alperen");
    GameController controller2;

    stat = controller2.play(game2, "testsIO/commands_game_over_2.dat");
    if (!stat) score += 1;

    // Out of commands
    BlockFall game3("testsIO/grid.dat", "testsIO/blocks_game_over_3.dat", false, "testsIO/leaderboard_tmp.txt", "alperen");
    GameController controller3;

    stat = controller3.play(game3, "testsIO/commands_game_over_3.dat");
    if (stat) score += 1;

    return score / (double) total_number;
}

int main(int argc, char **argv) {
    std::ofstream out_file("recorded_output.txt");
    std::streambuf* old_stdout_buf = std::cout.rdbuf(out_file.rdbuf());
    string gravity_mode(argv[4]);
    BlockFall game(argv[1], argv[2], (gravity_mode == "GRAVITY_ON"), argv[5], argv[6]);

    // Create a GameController instance
    GameController controller;

    // Play
    controller.play(game, argv[3]);
    std::cout.rdbuf(old_stdout_buf);
    return 0;
}